

import streamlit as st
import numpy as np
import matplotlib.pyplot as plt

def categorize_response_times(data):
    # Create the histogram using Matplotlib
    plt.figure(figsize=(25, 10))
    # Create the histogram
    counts, bins, patches = plt.hist(data, bins=range(1, 300, 10), edgecolor='black')
    # Rotate the x-axis labels for better readability
    plt.xticks(bins, rotation=45)
    # Set y-axis to have more frequency designations
    plt.yticks(np.arange(0, 251, 15)) 
    plt.title('Response Time Histogram')
    plt.xlabel('Response Time (s)')
    plt.ylabel('Frequency')
    
    # Display the plot
    st.pyplot(plt)

